/* Document parser helper function */
;documentHtml = function(html){
	var result = String(html)
		.replace(/<\!DOCTYPE[^>]*>/i, '')
		.replace(/<(html|head|body|title|meta|script)([\s\>])/gi,'<div class="document-$1"$2')
		.replace(/<\/(html|head|body|title|meta|script)\>/gi,'</div>')
	;
	return result;
};

;(function($) {
	var $window = $(window),
		$body = $('body'),
		winHeight = $(window).height(),
		$wizard = $('.wizard');

	$('.container-content').css({minHeight: winHeight - $('header').outerHeight() - $('footer').outerHeight()});

	if( typeof $wizard != 'undefined' && $wizard.length > 0 ) {
		$('.pace').css({
			zIndex: 9999
		});
	}

	$('.sortable ul').each(function(i, el) {
		var sortable = Sortable.create(el, {
			animation: 150,
			onSort: updateSort
		});
	});

	function updateSort() {
		var sort = '';

		$('.sortable li').each(function(i, el) {
			sort = (sort + $(el).data('item') + ','); 
		});

		$('.sortable-value').val(sort.slice(0, -1));
	}


  	$('.toggle').on('click', function(e) {
    	var $this = $(this);

    	$($this.data('selector')).toggleClass($this.data('class'));
    
    	e.preventDefault();
  	});

	var strokes = [
		'rgba(91, 144, 191, 1)',
		'rgba(91, 144, 191, 0)',
		'rgba(192, 78, 78, 0)',
		'rgba(125, 177, 81, 1)',
		'rgba(192, 78, 78, 1)'
	];

	var fills = [
		'rgba(199, 199, 199, 0)',
		'rgba(91, 144, 191, 0.1)',
		'rgba(255, 255, 255, 1)',
		'rgba(125, 177, 81, 0)',
		'rgba(192, 78, 78, 0.1)'
	];

	$('.chart').each(function(i, e) {
		var $chart = $(e),
			ctx = $chart.get(0).getContext("2d"),
			sets = $chart.data('sets'),
			label = $chart.data('label'),
			index, datasets = [];

		for (index = 0; index < sets.length; ++index) {
			datasets.push({
				fillColor: fills[index],
				strokeColor: strokes[index],
				lineColor: strokes[index],
				pointColor: strokes[index],
				pointHighlightFill: strokes[index],
				data: sets[index],
				label: label[index],
			});
		}

		window.myLine = new Chart(ctx).Line({
			labels: $chart.data('labels'),
			datasets: datasets
		}, {
			responsive: true,
			scaleOverride: true,
			scaleShowGridLines: false,
			scaleSteps: 7,
			scaleStepWidth: 50000,
			scaleStartValue: -100000,
			scaleLabel: function(label){return  '$' + label.value.toString().replace(/\B(?=(\d{3})+(?!\d))/g, ",");},
			maintainAspectRatio: false,
			pointDot: false,
			showTooltips: false,
			tooltipTitleFontSize: 0,
			tooltipTemplate: function(label){return  '$' + label.value.toString().replace(/\B(?=(\d{3})+(?!\d))/g, ",");},
			tooltipFillColor: "rgba(0,0,0,0.8)",
			legendTemplate : '<div class="overview-legenda"><ul>'
                  +'<% for (var i=0; i<datasets.length; i++) { %>'
                    +'<li>'
                    +'<span style=\"background-color:<%=datasets[i].strokeColor%>\"></span>'
                    +'<% if (datasets[i].label) { %><%= datasets[i].label %><% } %>'
                  +'</li>'
                +'<% } %>'
              +'</ul></div>'
        });

		var legend = window.myLine.generateLegend();

		$chart.parents('.overview-chart').before(legend);
	});

	$body.on('click', '.goals .block-choice', function() {
		$('.goals .active').removeClass('active');
		$(this).addClass('active');
		$('#goal').val($('.goals .active').data('value'));
	});

	$(".top").on("click", function(e) {
		$('html, body').animate({scrollTop : 0}, 300);
		e.preventDefault();
	});

	$('.menu').css({
		minHeight: ($window.height() - $('.top-lg').height())
	});

	$('[data-toggle="popover"]').popover();

	$('.switch button').on('click', function(e) {
		var $this = $(this),
			$switch = $this.parents('.switch');

		$switch.find('.active').removeClass('active');
		$($switch.data('targets')).addClass('hide');

		$this.addClass('active');
		$($this.data('target')).removeClass('hide');

		if( $switch.data('block') ) {
			if( $this.data('trigger') == 'add' )
				$($switch.data('block')).addClass('hide');
			else
				$($switch.data('block')).removeClass('hide');
		}

		e.preventDefault();
	});

	$body.on('click', '.next-step', function(e) {
		var $this = $(this);

		$.ajax({
			url: $this.attr('href'),
			success: function(data, textStatus, jqXHR) {
				var	$data = $($.parseHTML(documentHtml(data))),
					$dataBody = $data.find('.document-body:first'),
					title = $data.find('.document-title').html(),
					$dataProgress = $dataBody.find('.progress'),
					$dataBlock = $dataBody.find('.block');
				
				$dataProgress.find('li').each(function(i, e) {
					$('.progress li:nth-child(' + (i+1) + ')').attr('class', $(e).attr('class'));
				});

				$dataBlock.addClass('block-show');

				$('.block').addClass('block-hide')

				setTimeout(function() {
					$('.block').hide().after($dataBlock).hide(function() {
						$('.block-hide').remove();
						$('.block-show').removeClass('block-show');
					});
				}, 300);
			}
		});

		e.preventDefault();
	});

	$('#files-modal').on('shown.bs.modal', function (e) {
		var $modal = $(this),
			$bar = $modal.find('.progress-bar'),
			percentage = 0;

		var loading = setInterval(function() {
			$bar.css({
				width: ++percentage + '%'
			});

			if( percentage >= 101 ) {
				clearInterval(loading);
				$modal.find('.btn').removeAttr('disabled');
				$modal.find('.loading, .success').toggleClass('hide');
			}
		}, 50);
	});

})(window.jQuery);